  `rob_start_time` int(11) DEFAULT '0' COMMENT '开抢时间（展示开始时间+预热展示时间）',
  `total_pay` decimal(10,2) DEFAULT NULL COMMENT '应付总额',
  `pay` decimal(10,2) DEFAULT NULL COMMENT '实付总额',
