var storage = window.localStorage;
var minisnsId = $("#hidminisnsId").val();
var title_key = minisnsId + "_txtTitle";
var content_key = minisnsId + "_txtContentAdd";
var addArticle_div_key = minisnsId + "_farticle_form";
function addStorage() {
    storage.setItem(title_key, $("#txtTitle").val());
    storage.setItem(content_key, $("#txtContentAdd").val());
    storage.setItem(addArticle_div_key, $("#farticle").html())
}
function clearStorage() {
    storage.removeItem(title_key);
    storage.removeItem(content_key);
    storage.removeItem(addArticle_div_key)
}
$(function() {
    $("#txtTitle").bind("input propertychange", function() {
        addStorage()
    });
    $("#txtContentAdd").bind("input propertychange", function() {
        addStorage()
    })
});
if (storage.getItem(addArticle_div_key) != null ) {
    $("#farticle").html(storage.getItem(addArticle_div_key));
    if ($("#farticle #value-add-services").length > 0) {
        $("#farticle #value-add-services").remove()
    }
}
if (storage.getItem(title_key) != null ) {
    $("#txtTitle").val(storage.getItem(title_key))
}
if (storage.getItem(content_key) != null ) {
    $("#txtContentAdd").val(storage.getItem(content_key))
}
;